# le o arquivo, formate e verifica se Ã© multiplo de 16
def ChecaSeEhMultiplo():
	f = open("original.txt", "r")
	lines = f.readlines()
	msg = ''
	for line in lines:
		msg += (line[:len(line)-1])
	print(msg)
	print(len(msg))
	print(len(msg)%16)

